
// https://api.heroku.com/apps/$APP_ID_OR_NAME/addons
// https://devcenter.heroku.com/articles/platform-api-reference#plan

module.exports = function(state, action, callback) {
  var appId = action.input['App ID']
  var plan = action.input['Addon plan']

  state.dispatch({
    type: 'Request JSON',
    input: {
      URL: 'https://api.heroku.com/apps/' + appId + '/addons',
      Method: 'POST',
      Body: {
        plan: plan
      }
    }
  }, callback)
}
