
// https://devcenter.heroku.com/articles/platform-api-reference
// https://devcenter.heroku.com/articles/platform-api-reference#app
// https://devcenter.heroku.com/articles/platform-api-reference#build
// https://devcenter.heroku.com/articles/platform-api-reference#app-setup

module.exports = function(state, action, callback) {
  var appName = action.input['App name']
  var region = action.input['App region'] || 'us'

  state.dispatch({
    type: 'Request JSON',
    input: {
      Method: 'POST',
      Body: {
        name: appName,
        region: region
      }
    }
  }, callback)
}
