
// http://docs.aws.amazon.com/toolkit-for-eclipse/v1/user-guide/lambda-tutorial.html

module.exports = function(state, action, callback) {
  state.dispatch({
    type: 'Run process',
    input: {
      Actions: [
        {
          type: 'Lambda build function'
        },
        {
          type: 'S3 upload to bucket',
          input: {
            'Input file name': 'plugin.zip'
          }
        },
        {
          type: 'Ping lambda'
        }
      ]
    }
  }, callback)
}
