
module.exports = function(state, action) {
  var contentType = action.input['Content type'] // image/jpeg
  var bucket = action.input['S3 bucket name']
  var body = action.input['Upload content']
  var key = action.input['S3 file path']
  var isPublic = action.input['Is public']

  state.dispatch({ type: 'S3 initialize' }, function(err, S3){
    S3.putObject({
      Body: body,
      Bucket: bucket,
      ContentType: contentType,
      Key: key,
      ACL: 'public-read'
    })
  })
}
