
/**
 * Adds extra properties to an action on the backend.
 *
 * type: 'Enhance action'
 * input:
 *   Properties:
 *     - Stripe secret: 'foo'
 *   Action: 'List stripe charge'
 */

module.exports = function(state, action) {
  var enhancedAction = action.input.Action
  var properties = action.input.Properties
  var api = state.api

  api.enhancer(enhancedAction, properties)
}
